    private void btnIniciarSesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarSesionActionPerformed

        if (txtUsuario.getText().equals("") || txtContrasenia.getText().equals("")) {
            JOptionPane.showMessageDialog(rootPane, "los campos estan vacios");
 
        } else {
            boolean valor = udao.login(txtUsuario.getText(), txtContrasenia.getText());
            if (valor) {
                frmPersona viwPersona = new frmPersona();
                viwPersona.setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Error con el usuario o la contrase√±a");
            }

        }


    }//GEN-LAST:event_btnIniciarSesionActionPerformed

